def create_curve (shape='', name='', color='yellow', scale=[1.0, 1.0, 1.0]):

    """
    @param shape = str, give shape name you want in the valid args
    @param name = str, name of the curve
    @param color = str, give color name you want in the valid args
    @param scale = list, scale [X, Y, Z] for the curve
    
    @return list [str: curve name, str: curve shape name]

    DO :
    create a custom curve
    """


    shape_functions = {
    'circle': circle,
    'square': square,
    'locator': locator,
    'cube': cube,   
    'rtriangle': rtriangle,
    'triangle': triangle,
    'v': v,
    't': t,
    's': s,
    'c': c,
    'd': d,
    'f': f,
    'sphere': sphere,
    'smoothSphere': smoothSphere,
    'keyZ': keyZ,
    'keyX': keyX,
    'keyY': keyY,
    'orient': orient,
    'aimbase': aimbase,
    'arrow': arrow,
    'starSimple': starSimple,
    'cross': cross,
    'line': line,
    'eye': eye,
    'eyeY': eyeY,
    'eyeZ': eyeZ,
    'world' : world,
    'hexagon':hexagon,
    'solar': solar,
    'soft_cross': soft_cross,
    'hamster': hamster,
    'gear': gear,
    'boobs': boobs,
    'croissant': croissant,
    'flattenCube': flattenCube,
    'broken_hex': broken_hex, 
    'arrow_thick': arrow_thick,
    'mask': mask
    }
    
    curve = shape_functions[shape](scale=scale)

    if name :
        curve = cmds.rename(curve, name)

    curve_shape = cmds.listRelatives(curve, s=1)

    controlers.ctrl_color(curve, color=color)


    return curve, curve_shape

def get_cvs_position (curve=''):

    """
    @param shape = give curve shape where to look up

    @return pos: list, position of all cvs
    """


    pos = cmds.getAttr(curve + '.cv[*]')
    last_point = pos[0]
    pos.append(last_point)
    
    return pos

def circle (normal='z', scale=[1, 1, 1], degree=3):
    
    normalD = {'x':[1, 0, 0],'y':[0, 1, 0], 'z':[0, 0, 1]}
    new = cmds.circle(nr=normalD[normal], ch=0, r=0.6, d=degree)[0]
    
    shapes.scale(new, scale)
    
    return new

