def smoothSphere (scale=[1, 1, 1]):
    
    # curve 01

    new1 = circle(normal='x', scale = scale)
    cmds.makeIdentity(new1, a=True, r=True)

    # curve 02
    
    new2 = circle(normal='y', scale = scale)
    cmds.makeIdentity(new2, a=True, r=True)

    # curve 03
    
    new3 = circle(normal='z', scale = scale)
    cmds.makeIdentity(new3, a=True, r=True)


    # parent shapes

    for temp in [new2, new3]:
        newShape = cmds.listRelatives(temp, s=True)[0]
        cmds.parent(newShape, new1, r=True, s=True)
        cmds.delete(temp)


    return new1

