def keyX (scale=[1, 1, 1]):
    
    """
    key shape looks like circle and stick, where stick points to zero position
    Shape is oriented to +X axis with length of 1.0 unit
    """
    
    new = keyZ(scale)
    cmds.rotate(0, 90, 0, new)
    cmds.makeIdentity(new, a=True, r=True)
    
    return new

